BEGIN TRANSACTION;

DROP TABLE IF EXISTS users_supplies CASCADE;
DROP TABLE IF EXISTS supplies_alternate_names CASCADE;
DROP TABLE IF EXISTS projects_supplies CASCADE;
DROP TABLE IF EXISTS projects CASCADE;
DROP TABLE IF EXISTS supplies CASCADE;
DROP TABLE IF EXISTS users CASCADE;

DROP SEQUENCE IF EXISTS seq_user_id;

CREATE SEQUENCE seq_user_id
  INCREMENT BY 1
  NO MAXVALUE
  NO MINVALUE
  CACHE 1;

CREATE TABLE users (
	user_id int DEFAULT nextval('seq_user_id'::regclass) NOT NULL,
	username varchar(50) NOT NULL,
	password_hash varchar(200) NOT NULL,
	role varchar(50) NOT NULL,
	CONSTRAINT PK_user PRIMARY KEY (user_id)
);

CREATE TABLE supplies (
	supply_id SERIAL UNIQUE NOT NULL,
	supply_name varchar(50) NOT NULL,
	CONSTRAINT PK_supplies PRIMARY KEY (supply_id)
);

CREATE TABLE projects (
	project_id SERIAL UNIQUE NOT NULL,
	project_name varchar(50) NOT NULL,
	project_img varchar(200) NOT NULL,
	project_instructions varchar(200) NOT NULL,
	age_range varchar(10),
	season varchar(25),
	CONSTRAINT PK_projects PRIMARY KEY (project_id)
);

CREATE TABLE projects_supplies (
	id SERIAL UNIQUE NOT NULL,
	supply_id int NOT NULL,
	project_id int NOT NULL,
	color varchar(25),
	notes varchar(500),	
	optional boolean NOT NULL,
	CONSTRAINT PK_projects_supplies PRIMARY KEY (id),
	CONSTRAINT FK_projects_supplies_projects FOREIGN KEY (project_id) REFERENCES projects(project_id),
	CONSTRAINT FK_projects_supplies_supplies FOREIGN KEY (supply_id) REFERENCES supplies(supply_id)
);

CREATE TABLE supplies_alternate_names (
	supply_id int NOT NULL,
	alternate_name varchar(50) NOT NULL,
	CONSTRAINT PK_supplies_alternate_names PRIMARY KEY (supply_id, alternate_name),
	CONSTRAINT FK_supplies_alternate_names FOREIGN KEY (supply_id) REFERENCES supplies(supply_id)
);

CREATE TABLE users_supplies (
	craftbox_id SERIAL UNIQUE NOT NULL,
	supply_id int NOT NULL,
	user_id int NOT NULL,
	color varchar(25),
	notes varchar(500),	
	CONSTRAINT PK_users_supplies PRIMARY KEY (craftbox_id),
	CONSTRAINT FK_users_supplies_users FOREIGN KEY (user_id) REFERENCES users(user_id),
	CONSTRAINT FK_users_supplies_supplies FOREIGN KEY (supply_id) REFERENCES supplies(supply_id)
);


CREATE VIEW total_supplies AS (select project_id, count(supply_id) as supply_count
	from projects_supplies
	group by project_id);


CREATE VIEW owned_supplies AS (select project_id, count(us.supply_id) as supplies_owned, user_id
from projects_supplies
join users_supplies us on us.supply_id = projects_supplies.supply_id
group by project_id, user_id);










